#define GAMERTAG_CHARS NUMERIC_CHARS ENGLISH_CHARS EXTENDED_CHARS ASIAN_CHARS
#define FILENAME_CHARS L"ABCDEFGHIKJLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz" NUMERIC_CHARS L"_ ()&[]{}@'#£$%"

	m_ValidCharacters.clear();
	int iCount = 0;

